/* Reset & standardize default styles */
@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css") layer;

/* Design tokens (apply to components) */
:root {
  /* Custom theme options */
  --color-accent: black;
  --color-accent2: #999999;
  --color-shade: #2d3339;

  --inner-max-width: 800px;

  /* Base values */
  --box-shadow: 0px 2px 8px rgba(0, 0, 0, 0.1);
  --border-radius: 2px;
  --border-color: #e0e1e1;
  --body-font: "Meriweather", serif;
  --heading-font: "Inter", sans-serif;
}

/* Root element (use instead of `body`) */
#page {
  font-family: var(--heading-font);
  color: #111;
  line-height: 1.5;
  font-size: 1.125rem;
  background: white;
}

/* Elements */
.section-container {
  max-width: 1150px;
  margin: 0 auto;
  padding: 5rem 2rem;
}

a.link {
  line-height: 1.3;
  border-bottom: 2px solid var(--color-accent);
  transform: translateY(-2px); /* move link back into place */
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: transparent;
  }
}

.heading {
  font-size: clamp(2rem, 10vw, 3rem);
  line-height: 1.1;
  font-weight: 600;
  letter-spacing: -0.15rem;
}

.button {
  color: white;
  background: var(--color-accent, rebeccapurple);
  border-radius: 0;
  padding: 1px 24px;
  transition: var(--transition, 0.1s box-shadow);
  border: 0; /* reset */
  border-radius: 0.25rem;
  font-size: 1rem;

  &:hover {
    box-shadow: 0 0 0 2px var(--color-accent, rebeccapurple);
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent, rebeccapurple);
  }
}

section {
  display: grid;
  place-items: center;
  max-width: var(--inner-max-width, 900px);
  padding-bottom: 3rem;
}

.heading {
  text-align: center;
  text-wrap: balance;
  margin-bottom: 3rem;
}

figure {
  display: grid;
  gap: 0.25rem;
  margin-bottom: 2.5rem;
  img {
    width: 100px;
    height: 100px;
    object-fit: cover;
    border-radius: 100%;
  }
  figcaption {
    color: var(--color-shade, #2d3339);
    font-size: 0.875rem;
    font-weight: 400;
    text-align: center;

  }
}
.content {
    line-height: 1.5;
    font-family: var(--body-font);
    font-weight: 300;
    color: #2d3339;

  :global(p){
    margin-bottom: 1rem;
  }

}

